import numpy as np

"""Lasso Regression"""

# creating a class for Lasso Regression

class Lasso_Regression():

  #initiating the hyperparameters
  def __init__(self, learning_rate, no_of_iterations, lambda_parameter):

    self.learning_rate = learning_rate
    self.no_of_iterations = no_of_iterations
    self.lambda_parameter = lambda_parameter


  # fitting the dataset to the Lasso Regression model
  def fit(self, X, Y):

    # m --> number of Data points --> number of rows
    # n --> number of input features --> number of columns
    self.m, self.n = X.shape

    self.w = np.zeros(self.n)

    self.b = 0

    self.X = X

    self.Y = Y

    # implementing Gradient Descent algorithm for Optimization

    for i in range(self.no_of_iterations):     # missed "self"
      self.upadte_weights()


  # function for updating the weight & bias value
  def upadte_weights(self):

    # linear equation of the model
    Y_prediction = self.predict(self.X)

    # gradients (dw, db)

    # gradient for weight
    dw = np.zeros(self.n)

    for i in range(self.n):

      if self.w[i]>0:

        dw[i] = (-(2*(self.X[:,i]).dot(self.Y - Y_prediction)) + self.lambda_parameter) / self.m

      else :

        dw[i] = (-(2*(self.X[:,i]).dot(self.Y - Y_prediction)) - self.lambda_parameter) / self.m


    # gradient for bias
    db = - 2 * np.sum(self.Y - Y_prediction) / self.m


    # updating the weights & bias

    self.w = self.w - self.learning_rate*dw
    self.b = self.b - self.learning_rate*db




  # Predicting the Target variable
  def predict(self,X):

    return X.dot(self.w) + self.b

"""Using Linear Regression model for Prediction

"""

# importing the dependencies
import numpy as np
import pandas as pd
from sklearn.model_selection import train_test_split
import matplotlib.pyplot as plt
from sklearn import metrics

"""Data Pre-Processing"""

# loading the data from csv file to a pandas dataframe

salary_data = pd.read_csv('/content/salary_data.csv')

# printing the first 5 columns of the dataframe
salary_data.head()

# last 5 rows of the dataframe
salary_data.tail()

# number of rows & columns in the dataframe
salary_data.shape

# checking for missing values
salary_data.isnull().sum()

"""Splitting the feature & target"""

features = salary_data.iloc[:,:-1].values
target = salary_data.iloc[:,1].values

print(features)

print(target)

"""Splitting the dataset into training & test data"""

X_train, X_test, Y_train, Y_test = train_test_split(features, target,
                                                    test_size=0.33,
                                                    random_state = 2)

"""
Training the Linear Regression model"""

model = Lasso_Regression(learning_rate = 0.02, no_of_iterations=1000,
                         lambda_parameter=200)

model.fit(X_train, Y_train)

"""Predict the salary value for test data"""

test_data_prediction = model.predict(X_test)

print(test_data_prediction)

from sklearn import metrics

# R squared error
score_1 = metrics.r2_score(Y_test, test_data_prediction)

# Mean Absolute Error
score_2 = metrics.mean_absolute_error(Y_test, test_data_prediction)

print("R squared error : ", score_1)
print('Mean Absolute Error : ', score_2)

"""Comparing the error with SKLearn Lasso Regression"""

from sklearn.linear_model import Lasso
sk_model = Lasso()

sk_model.fit(X_train, Y_train)

sk_test_data_prediction = sk_model.predict(X_test)

# R squared error
score_1_ = metrics.r2_score(Y_test, sk_test_data_prediction)

# Mean Absolute Error
score_2_ = metrics.mean_absolute_error(Y_test, sk_test_data_prediction)

print("R squared error : ", score_1_)
print('Mean Absolute Error : ', score_2_)